/*
侧边导航
*/
import React, { Component } from 'react';
import {Link} from 'react-router-dom'
import { Menu } from 'antd'
import {
    AppstoreOutlined,
    MenuUnfoldOutlined,
    PieChartOutlined,
    MailOutlined,
  } from '@ant-design/icons'
import routeList from '../../router/menu'
  const { SubMenu } = Menu
class LeftNav extends Component {
    //根据routeList生成对应的标签组件
    getMenuNodes = (routeList) => {
        // eslint-disable-next-line array-callback-return
        routeList.map(item => {
            if(!item.children){
                return(
                    <Menu.Item key={item.path} icon={<PieChartOutlined />}>
                        <Link to={item.path}>
                            {item.name}
                        </Link>

                    </Menu.Item>
                )
            }else{
                return(
                    <SubMenu key="sub1" icon={<MailOutlined />} title={item.name}>
                        {/* <Menu.Item key="/shop" icon={<AppstoreOutlined />}>
                            <Link to='/shop'>
                            品类管理
                            </Link>

                        </Menu.Item> */}
                        {
                            this.getMenuNodes(item.children)
                        }


                    </SubMenu>
                )

            }
        })
    }
    render() {
        return (
            <div>
                <div className="top">
                    头部
                </div>
                <Menu
                // defaultSelectedKeys={['1']}
                // defaultOpenKeys={['sub1']}
                mode="inline"
                theme="dark"
                >

                { 
                    console.log(this.getMenuNodes(routeList))
                }


                <Menu.Item key="/home" icon={<PieChartOutlined />}>
                    <Link to='/home'>
                        首页
                    </Link>

                </Menu.Item>
                <SubMenu key="sub1" icon={<MailOutlined />} title="商品">
                    <Menu.Item key="/shop" icon={<AppstoreOutlined />}>
                    <Link to='/shop'>
                    品类管理
                    </Link>

                    </Menu.Item>
                    <Menu.Item key="/shopping" icon={<MenuUnfoldOutlined />}>
                    <Link to='/shopping'>
                    商品管理
                    </Link>
                    </Menu.Item>

                </SubMenu>
                <Menu.Item key="/user" icon={<PieChartOutlined />}>
                    <Link to='/user'>
                        用户管理
                    </Link>

                </Menu.Item>
                <Menu.Item key="/role" icon={<PieChartOutlined />}>
                    <Link to='/role'>
                        角色管理
                    </Link>

                </Menu.Item>
                <SubMenu key="sub2" icon={<MailOutlined />} title="数据">
                    <Menu.Item key="/charts/bar" icon={<AppstoreOutlined />}>
                    <Link to='/charts/bar'>
                        圆柱体
                    </Link>

                    </Menu.Item>
                    <Menu.Item key="/charts/pie" icon={<MenuUnfoldOutlined />}>
                    <Link to='/charts/pie'>
                        折线图
                    </Link>
                    </Menu.Item>
                </SubMenu>
                </Menu>
                LeftNav
            </div>
        );
    }
}

export default LeftNav;
