/*
侧边导航
*/
import React, { Component } from 'react';
import {Link} from 'react-router-dom'
import { Menu } from 'antd'
import {
    // AppstoreOutlined,
    // MenuUnfoldOutlined,
    PieChartOutlined,
    MailOutlined,
  } from '@ant-design/icons'
import routeList from '../../router/menu'
  const { SubMenu } = Menu
class LeftNav extends Component {
    //根据routeList生成对应的标签组件
    getMenuNodes = (routeList) => {
        // eslint-disable-next-line array-callback-return
        return routeList.map(item => {
            if(!item.children){
                return(
                    <Menu.Item key={item.path} icon={<PieChartOutlined />}>
                        <Link to={item.path}>
                            {item.name}
                        </Link>

                    </Menu.Item>
                )
            }else{
                return(
                    <SubMenu key={item.name} icon={<MailOutlined />} title={item.name}>
                        {/* <Menu.Item key="/shop" icon={<AppstoreOutlined />}>
                            <Link to='/shop'>
                            品类管理
                            </Link>

                        </Menu.Item> */}
                        {
                            this.getMenuNodes(item.children)
                        }


                    </SubMenu>
                )

            }
        })
    }
    render() {
        return (
            <div>
                <div className="top">
                    头部
                </div>
                <Menu
                // defaultSelectedKeys={['1']}
                // defaultOpenKeys={['sub1']}
                mode="inline"
                theme="dark"
                >

                { 
                   this.getMenuNodes(routeList)
                }


                </Menu>
                LeftNav
            </div>
        );
    }
}

export default LeftNav;
